import React from "react";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import "./Slider.css";
import Slider1 from "../images/slider-1.jpg";
import Slider2 from "../images/slider-2.jpg";
import Slider3 from "../images/slider-3.jpg";
import Slider4 from "../images/slider-4.jpg";
import Slider5 from "../images/slider-5.jpg";

export default class Slider extends React.Component {
  componentDidMount() {
    const $ = window.jQuery;
    $(".set-bg").each(function() {
      var bg = $(this).data("setbg");
      console.log(bg);
      $(this).css("background-image", "url(" + bg + ")");
    });
  }

  render() {
    return (
      <section class="hero-section">
        <OwlCarousel className="owl-theme home-page" loop margin={10} items={1} autoplay dots={false}>
          <div class="hero-slider item">
            <div className="hs-item set-bg" data-setbg={Slider1}>
              <div className="hs-text">
                <div >
                  <h2>
                    The Best <span>Dice</span> Game
                  </h2>
                  <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec malesuada <br /> lorem maximus mauris
                    scelerisque, at rutrum nulla dictum. Ut ac ligula sapien. <br />
                    Suspendisse cursus faucibus finibus.
                  </p>
                  <a href="#" className="site-btn">
                    Play Now
                  </a>
                </div>
              </div>
            </div>
          </div>
          <div className="item hg-item set-bg" data-setbg={Slider2}>
            {/* <img src={Slider2} className="hg-bg"/> */}
          </div>
          <div className="item hg-item set-bg" data-setbg={Slider3}>
            {/* <img src={Slider3} className="hg-bg"/> */}
          </div>
          <div className="item hg-item set-bg" data-setbg={Slider4}>
            {/* <img src={Slider4} className="hg-bg"/> */}
          </div>
          <div className="item hg-item set-bg" data-setbg={Slider5}>
            {/* <img src={Slider5} className="hg-bg"/> */}
          </div>
        </OwlCarousel>
      </section>
    );
  }
}
